package org.projet.cabinet.model; import java.io.Serializable; import java.time.LocalDateTime;
public class Bilan implements Serializable {
    private int id; private Patient patient; private Utilisateur medecin; private String motif; private String observations; private String diagnostic; private String traitement; private LocalDateTime dateBilan;
    public Bilan() {} public Bilan(int id, Patient patient, Utilisateur medecin, String motif, String observations, String diagnostic, String traitement, LocalDateTime dateBilan) { this.id = id; this.patient = patient; this.medecin = medecin; this.motif = motif; this.observations = observations; this.diagnostic = diagnostic; this.traitement = traitement; this.dateBilan = dateBilan; }
    public int getId() { return id; } public void setId(int id) { this.id = id; } public Patient getPatient() { return patient; } public void setPatient(Patient patient) { this.patient = patient; } public Utilisateur getMedecin() { return medecin; } public void setMedecin(Utilisateur medecin) { this.medecin = medecin; }
    public String getMotif() { return motif; } public void setMotif(String motif) { this.motif = motif; } public String getObservations() { return observations; } public void setObservations(String observations) { this.observations = observations; } public String getDiagnostic() { return diagnostic; } public void setDiagnostic(String diagnostic) { this.diagnostic = diagnostic; } public String getTraitement() { return traitement; } public void setTraitement(String traitement) { this.traitement = traitement; } public LocalDateTime getDateBilan() { return dateBilan; } public void setDateBilan(LocalDateTime dateBilan) { this.dateBilan = dateBilan; }
}